targets.expr:
sum by (pod) (
  kube_pod_container_resource_requests{resource="cpu", unit="core"}
  * on(pod) group_left()
  kube_pod_status_phase{phase="Running"}
)

sum by (pod) (
  kube_pod_container_resource_requests{resource="memory", unit="byte"}
  * on(pod) group_left()
  kube_pod_status_phase{phase="Running"}
)
